mapper.select:
select
id , op_user_id , remark , sex , account , op_time , status , phone , email , name , password from sys_user t
where 1=1
 and t.id = #{id } 
 and t.op_user_id = #{opUserId } 
 and t.remark = #{remark } 
 and t.sex = #{sex } 
 and t.account = #{account } 
 and t.op_time = #{opTime } 
 and t.status = #{status } 
 and t.phone = #{phone } 
 and t.email = #{email } 
 and t.name = #{name } 
 and t.password = #{password }

mapper.delete:
delete from sys_user t where
in

	#{id}
